(function ($) {
    "use strict";

    // Variable to store the selected department ID
    let selectedDepartmentId = $('select[name="department_id"]').val();

    // Event listener for department select change
    $('body').on('change', 'select[name="department_id"]', function () {
        selectedDepartmentId = $(this).val();
    });

    // Event listener for add major button click
    $('body').on('click', '.add-major-btn', function (e) {
        e.preventDefault();
        var dataId = $(this).attr('data-id');

        if (selectedDepartmentId) { 
            // Show loading Swal
            Swal.fire({
                title: 'Adding Major',
                text: 'Please wait while the major is being added...',
                icon: 'info',
                allowOutsideClick: false,
                didOpen: () => {
                    Swal.showLoading();
                    // Set the z-index of Swal modal
                    Swal.getPopup().style.zIndex = 20000;
                }
            });

            $.ajax({
                url: '/admin/universities/' + universityId + '/majors',
                type: 'POST',
                data: {
                    major_id: dataId,
                    department_id: selectedDepartmentId
                },
                headers: {
                    'X-CSRF-TOKEN': $('meta[name="csrf-token"]').attr('content'),
                },
                success: function (response) {
                    Swal.close(); // Close the loading Swal
                    if (response.success) {
                        Swal.fire({
                            icon: 'success',
                            title: 'Major Added',
                            text: 'The major has been successfully added.',
                            didOpen: () => {
                                // Set the z-index of Swal modal
                                Swal.getPopup().style.zIndex = 20000;
                            }
                        });
                    } else {
                        Swal.fire({
                            icon: 'error',
                            title: 'Error',
                            text: response.message,
                            didOpen: () => {
                                // Set the z-index of Swal modal
                                Swal.getPopup().style.zIndex = 20000;
                            }
                        });
                    }
                },
                error: function (xhr) {
                    Swal.close(); // Close the loading Swal
                    Swal.fire({
                        icon: 'error',
                        title: 'Error',
                        text: 'An error occurred while adding the major. Please try again later.',
                        didOpen: () => {
                            // Set the z-index of Swal modal
                            Swal.getPopup().style.zIndex = 20000;
                        }
                    });
                    console.error('Error:', xhr);
                }
            });
        } else {
            Swal.fire({
                icon: 'warning',
                title: 'No Department Selected',
                text: 'Please select a department before adding a major.',
                didOpen: () => {
                    // Set the z-index of Swal modal
                    Swal.getPopup().style.zIndex = 20000;
                }
            });
        }
    });

    // Event listener for form submission
    $('#add-topic-form').on('submit', function(e) {
        e.preventDefault();
        var form = $(this);
        var formData = form.serialize();

        Swal.fire({
            title: 'Adding Topic',
            text: 'Please wait while the topic is being added...',
            icon: 'info',
            allowOutsideClick: false,
            showConfirmButton: false,
            didOpen: () => {
                Swal.showLoading();
                Swal.getPopup().style.zIndex = 20000;
            }
        });

        $.ajax({
            url: form.attr('action'),
            type: 'POST',
            data: formData,
            headers: {
                'X-CSRF-TOKEN': $('meta[name="csrf-token"]').attr('content'),
            },
            success: function(response) {
                Swal.close();
                if (response.success) {
                    Swal.fire({
                        icon: 'success',
                        title: 'Topic Added',
                        text: 'The topic has been successfully added.',
                        didOpen: () => {
                            Swal.getPopup().style.zIndex = 20000;
                        }
                    });

                    $('#syllabus-topics-table-body').append(
                        '<tr>' +
                            '<td>' + response.topic.title + '</td>' +
                            '<td>' + response.topic.description + '</td>' +
                            '<td>' +
                                // '<button class="btn btn-sm btn-primary edit-topic-btn" data-id="' + response.topic.id + '">Edit</button>' +
                                '<a href="javascript:void(0)" class="delete-topic" data-id="' + response.topic.id + '"><i class="fa fa-trash"></i></a>' +
                            '</td>' +
                        '</tr>'
                    );

                    form[0].reset();
                } else {
                    Swal.fire({
                        icon: 'error',
                        title: 'Error',
                        text: response.message,
                        didOpen: () => {
                            Swal.getPopup().style.zIndex = 20000;
                        }
                    });
                }
            },
            error: function(xhr) {
                Swal.close(); // Close the loading Swal
                Swal.fire({
                    icon: 'error',
                    title: 'Error',
                    text: 'An error occurred while adding the topic. Please try again later.',
                    didOpen: () => {
                        // Set the z-index of Swal modal
                        Swal.getPopup().style.zIndex = 20000;
                    }
                });
                console.error('Error:', xhr);
            }
        });
    });

    $(document).on('click', '.delete-topic', function (e) {
        e.preventDefault();
        var topicId = $(this).data('id');

        Swal.fire({
            title: 'Are you sure?',
            text: "You won't be able to revert this!",
            icon: 'warning',
            showCancelButton: true,
            confirmButtonColor: '#3085d6',
            cancelButtonColor: '#d33',
            confirmButtonText: 'Yes, delete it!',
            didOpen: () => {
                Swal.getPopup().style.zIndex = 20000;
            }
        }).then((result) => {
            if (result.isConfirmed) {
                Swal.fire({
                    title: 'Deleting...',
                    text: 'Please wait while the topic is being deleted.',
                    icon: 'info',
                    allowOutsideClick: false,
                    showConfirmButton: false,
                    didOpen: () => {
                        Swal.showLoading();
                        Swal.getPopup().style.zIndex = 20000;
                    }
                });

                $.ajax({
                    url: '/admin/syllabus/topic/' + topicId + '/delete',
                    type: 'GET',
                    headers: {
                        'X-CSRF-TOKEN': $('meta[name="csrf-token"]').attr('content'),
                    },
                    success: function(response) {
                        Swal.close(); // Close the loading Swal
                        if (response.success) {
                            Swal.fire({
                                icon: 'success',
                                title: 'Deleted!',
                                text: 'The topic has been deleted.',
                                didOpen: () => {
                                    Swal.getPopup().style.zIndex = 20000;
                                }
                            });

                            $('a[data-id="' + topicId + '"]').closest('tr').remove();
                        } else {
                            Swal.fire({
                                icon: 'error',
                                title: 'Error',
                                text: response.message,
                                didOpen: () => {
                                    Swal.getPopup().style.zIndex = 20000;
                                }
                            });
                        }
                    },
                    error: function(xhr) {
                        Swal.close(); // Close the loading Swal
                        Swal.fire({
                            icon: 'error',
                            title: 'Error',
                            text: 'An error occurred while deleting the topic. Please try again later.',
                            didOpen: () => {
                                Swal.getPopup().style.zIndex = 20000;
                            }
                        });
                        console.error('Error:', xhr);
                    }
                });
            }
        });
    });

})(jQuery);
