(function ($) {
    "use strict";

    // Variable to store the selected department ID
    let selectedDepartmentId = null;

    // Event listener for department select change
    $('body').on('change', 'select[name="department_id"]', function () {
        selectedDepartmentId = $(this).val();
    });

    // Event listener for add major button click
    $('body').on('click', '.add-major-btn', function (e) {
        e.preventDefault();
        var dataId = $(this).attr('data-id');

        if (selectedDepartmentId) {
            $.ajax({
                url: '/admin/universities/' + universityId + '/majors',
                type: 'POST',
                data: {
                    major_id: dataId,
                    department_id: selectedDepartmentId
                },
                headers: {
                    'X-CSRF-TOKEN': $('meta[name="csrf-token"]').attr('content'),
                },
                success: function (response) {
                    if (response.success) {
                        Swal.fire({
                            icon: 'success',
                            title: 'Major Added',
                            text: 'The major has been successfully added.',
                        });
                    } else {
                        Swal.fire({
                            icon: 'error',
                            title: 'Error',
                            text: 'Failed to add the major. Please try again later.',
                        });
                    }
                },
                error: function (xhr) {
                    Swal.fire({
                        icon: 'error',
                        title: 'Error',
                        text: 'An error occurred while adding the major. Please try again later.',
                    });
                    console.error('Error:', xhr);
                }
            });
        } else {
            Swal.fire({
                icon: 'warning',
                title: 'No Department Selected',
                text: 'Please select a department before adding a major.',
            });
        }
    });

})(jQuery);
